<?php/** * SLIM_MVC_Framework * * @category  controllers * @package   SLIM_MVC_Framework * @copyright Copyright (c) 2014 (http://www.elamurugan.com/) * @author    Ela <nelamurugan@gmail.com> *//** * Class SLIM_MVC_Framework * * @category    controllers * @package     SLIM_MVC_Framework */class Block extends Template{    public $_templateFilePath = "";    public $_templateFile     = "";    public $_block     = false;    public function render()    {        if ($this->_templateFile == "") {            $templatePath  =  $this->_templateFilePath;        } else {            $templatePath = Config::$templatePath . $this->_templateFile;        }        include_once($templatePath);    }    public function getNodeParam()    {        $block = $this->_block;    }}