<?php/** * SLIM_MVC_Framework * * @category  controllers * @package   SLIM_MVC_Framework * @copyright Copyright (c) 2014 (http://www.elamurugan.com/) * @author    Ela <nelamurugan@gmail.com> *//** * Class SLIM_MVC_Framework * * @category    controllers * @package     SLIM_MVC_Framework */class Controller extends Template{    public function __construct()    {    }    public function redirectUrl($url)    {        if (!isset($_REQUEST['is_ajax'])) {            if (!headers_sent()) {                header("Location: " . $url . "");                exit;            } else {                echo "<script language='javascript' type='text/javascript'>window.location.href='" . $url . "'</script>";                echo "<META http-equiv='refresh' content='0;URL=" . $url . "'>";                exit;            }        }    }    public function redirect($url)    {        $baseUrlPath = _BASEURL;        if ($this->isAdminPath()) {            $baseUrlPath = $baseUrlPath . $this->getAdminRoutePath() . '/';        }        $this->redirectUrl($baseUrlPath . $url);    }    public function error404Action()    {        $this->setPageTitle("Error 404 Page Not found");        $this->renderHtml();    }}