<?php/** * SLIM_MVC_Framework * * @category  controllers * @package   SLIM_MVC_Framework * @copyright Copyright (c) 2014 (http://www.elamurugan.com/) * @author    Ela <nelamurugan@gmail.com> *//** * Class SLIM_MVC_Framework * * @category    controllers * @package     SLIM_MVC_Framework */class Error extends Controller{    public function printError($_className ='',$errorMsg = '')    {        global $__area, $app, $xmlObj, $dbObj;        $model = false;        $handler = 'exception_error404';        $this->__module = 'error';        $this->__action = 'printError';        $modelClass = ucfirst('Exception');        if ($modelClass != '' && class_exists($modelClass)) {            $model = new $modelClass;        }        $__area = $xmlObj->getConfig('default/frontend/area');        parent::$__area = $__area;        $this->template = $this;        $this->model = $model;        $this->_templateInit();        $this->addHandle($handler);        $this->setBodyClass($handler);        $this->renderHtml(array("msg" => $errorMsg));        exit();    }}