<?php/** * SLIM_MVC_Framework * * @category  controllers * @package   SLIM_MVC_Framework * @copyright Copyright (c) 2014 (http://www.elamurugan.com/) * @author    Ela <nelamurugan@gmail.com> *//** * Class SLIM_MVC_Framework * * @category    controllers * @package     SLIM_MVC_Framework */class Errorhandler extends Controller{    public function printError($_className ='',$errorMsg = '')    {        $model = false;        $handler = 'exception_error404';        $modelClass = ucfirst('Exception');        if ($modelClass != '' && class_exists($modelClass)) {            $model = new $modelClass;        }        $this->model = $model;        $this->_templateInit();        $this->addHandle($handler);        $this->setBodyClass($handler);        $this->renderHtml(array("msg" => $errorMsg));        exit();    }}