<?php 
class Template {    	public static $js_files = array();	public static $css_files = array();	public static $configData = array();	public static $skinPath = '';	public static $cachedJson = false;	public static $cacheJsonFile = '';		public function _init() { 		global $globalJsFiles,$globalCssFiles,$_app_params;        $area   = $_app_params['config']['area'];		$theme  = $_app_params['config'][$area]['theme'];        self::$skinPath = "skin/".$area."/".$theme."/";		if (!is_writable(_BASEDIR.self::$skinPath)) {			die("Please Check skin directory path is writeable");		}		foreach($globalJsFiles[$area] as $globalJsFile){			self::addJs($globalJsFile);		}		foreach($globalCssFiles[$area]  as $globalCssFile){			self::addCss($globalCssFile);		}		self::$cacheJsonFile = _BASEDIR.self::$skinPath."cache/cache.json";    }	
    public function redirect($url) {        if(!isset($_REQUEST['is_ajax'])){            if (!headers_sent()) {                header("Location: " . _BASEURL . $url . "");                exit;            } else {                echo "<script language='javascript' type='text/javascript'>window.location.href='" . _BASEURL . $url . "'</script>";                echo "<META http-equiv='refresh' content='0;URL=" . _BASEURL . $url . "'>";                exit;            }        }    }		public function error404Action($_app_params) {    	$this->render("error_404");    }	// For normal response    public function render($view_file ,$params = array()) {		        global $obj,$_app_params,$_statues,$_priority;          $area   = $_app_params['config']['area'];		$theme  = $_app_params['config'][$area]['theme'];        $module = $_app_params['config']['module'];		        $template_path = _BASEDIR."app/design/".$area."/".$theme; 				require_once($template_path."/header.phtml");        if (file_exists($template_path."/".$module."/".$view_file.".phtml"))		    require_once($template_path."/".$module."/".$view_file.".phtml");        else            require_once($template_path."/error_404.phtml");         require_once($template_path."/footer.phtml");		Helper::updateCacheFile(self::$cacheJsonFile,self::$cachedJson);    }        // For ajax kind of response    public function render_template($view_file, $params = array())    {        ob_start();        global $obj,$_app_params,$_statues,$_priority;         $area   = $_app_params['config']['area'];		$theme  = $_app_params['config'][$area]['theme'];        $module = $_app_params['config']['module'];        $template_path = _BASEDIR."app/design/".$area."/".$theme; 		        if (file_exists($template_path."/".$module."/".$view_file.".phtml"))		    require_once($template_path."/".$module."/".$view_file.".phtml");        else            require_once($template_path."/error_404.phtml");         $_template_content = ob_get_contents();        ob_end_clean();		Helper::updateCacheFile(self::$cacheJsonFile,self::$cachedJson);        return $_template_content;    }		public function setPageTitle($site_title)    {		global $_app_params; 		$_app_params['config']['site_title'] = $site_title;	}		public function setPageMetaKeywords($site_meta_keywords)    {		global $_app_params; 		$_app_params['config']['site_meta_keywords'] = $site_meta_keywords;	}		public function setPageMetadescription($site_meta_description)    {		global $_app_params; 		$_app_params['config']['site_meta_description'] = $site_meta_description;	}		public function getSiteTitle()    {		return self::$configData['site_title'];	}		public function getPageTitle()    {		global $_app_params; 		$title = $_app_params['config']['site_title']." - ".self::$configData['site_title'];		return $title;	}		public function getPageMetaKeywords()    {		global $_app_params;		$site_meta_keywords = @$_app_params['config']['site_meta_keywords']." ".self::$configData['site_meta_keywords'];		return $site_meta_keywords;	}		public function getPageMetadescription()    {		global $_app_params;		$site_meta_description = @$_app_params['config']['site_meta_description']." ".self::$configData['site_meta_description'];		return $site_meta_description;	} 			public function getBodyClass()    {		global $_app_params; 		return $_app_params['config']['body_class'];	}			public static function addJs($js_file)    {		self::$js_files[] = $js_file;	}		public static function addCss($css_file)    {		self::$css_files[] = $css_file;	}		public function renderJs()    {		$mergedJsFileName = $html = '';		self::$js_files = array_unique(self::$js_files);		if(self::$configData['js_compress'] == 1){			if(!is_dir(_BASEDIR.self::$skinPath."cache")) {				mkdir(_BASEDIR.self::$skinPath."cache", 0777);			}			$curentJsFiles['files'] = implode(",",self::$js_files); 			if(file_exists(self::$cacheJsonFile)){				if(self::$cachedJson === false){					self::$cachedJson = Helper::readCacheFile(self::$cacheJsonFile);				}				if(self::$cachedJson &&  is_array(self::$cachedJson) && isset(self::$cachedJson['js'])){					foreach(self::$cachedJson['js'] as $jsMergedFile){						if($curentJsFiles['files'] == $jsMergedFile['files']){							$mergedJsFileName =  $jsMergedFile['merged'];						}					}				}			}			// $mergedJsFileName =  self::$configData['compressed_js_file'];			if($mergedJsFileName == '' || !file_exists(_BASEDIR.self::$skinPath."cache/".$mergedJsFileName)){				global $dbObj;				include_once(_BASEDIR."lib/jsmin.php");				$mergedJsFileName = Helper::generateRandomString().'.js';				$jsContents = '';				foreach(self::$js_files as $jsFile){ 					if(file_exists(_BASEDIR.self::$skinPath.$jsFile)){						$jsContents .= file_get_contents(_BASEDIR.self::$skinPath.$jsFile);					}				}				$jsContents = JSMin::minify($jsContents);				$handle     = fopen(_BASEDIR.self::$skinPath."cache/".$mergedJsFileName, 'w');				fwrite($handle, $jsContents);				fclose($handle);				// $dbObj->updateConfigVariable('compressed_js_file',$mergedJsFileName); 								$curentJsFiles['merged'] = $mergedJsFileName;				self::$cachedJson['js'][] = $curentJsFiles; 			}			// debug(self::$cachedJson);			$html .= sprintf('<script type="text/javascript" src="%s"></script>' . "\n",_BASEURL.self::$skinPath."cache/".$mergedJsFileName);		}		else{			foreach(self::$js_files as $jsFile){ 				if(file_exists(_BASEDIR.self::$skinPath.$jsFile)){					$html .= sprintf('<script type="text/javascript" src="%s"></script>' . "\n",_BASEURL.self::$skinPath.$jsFile);				}			}		}				return $html;	}			public function renderCss()    {		$mergedCssFileName = $html = '';		self::$css_files = array_unique(self::$css_files);		if(self::$configData['css_compress'] == 1){			if(!is_dir(_BASEDIR.self::$skinPath."cache")) {				mkdir(_BASEDIR.self::$skinPath."cache", 0777);			}						$cssMergedFiles = array();			$curentCssFiles['files'] = implode(",",self::$css_files); 			if(file_exists(self::$cacheJsonFile)){				if(self::$cachedJson === false){					self::$cachedJson = Helper::readCacheFile(self::$cacheJsonFile);				}				$cssMergedFiles = self::$cachedJson;				if(self::$cachedJson && is_array(self::$cachedJson) && isset(self::$cachedJson['css'])){					foreach(self::$cachedJson['css'] as $cssMergedFile){						if($curentCssFiles['files'] == $cssMergedFile['files']){							$mergedCssFileName =  $cssMergedFile['merged'];						}					}				}			}			// $mergedCssFileName =  self::$configData['compressed_css_file'];			if($mergedCssFileName == '' || !file_exists(_BASEDIR.self::$skinPath."cache/".$mergedCssFileName)){				global $dbObj;				$mergedCssFileName = Helper::generateRandomString().'.css';				$cssContents = '';				foreach(self::$css_files as $cssFile){ 					if(file_exists(_BASEDIR.self::$skinPath.$cssFile)){						$cssContents .= file_get_contents(_BASEDIR.self::$skinPath.$cssFile);					}				}				$cssContents = Helper::compressCss($cssContents,_BASEURL.self::$skinPath);				$handle     = fopen(_BASEDIR.self::$skinPath."cache/".$mergedCssFileName, 'w');				fwrite($handle, $cssContents);				fclose($handle);				// $dbObj->updateConfigVariable('compressed_css_file',$mergedCssFileName);								$curentCssFiles['merged'] = $mergedCssFileName;				self::$cachedJson['css'][] = $curentCssFiles;			}			// debug(self::$cachedJson);			$html .= sprintf('<link rel="stylesheet" type="text/css" href="%s"/>'."\n",_BASEURL.self::$skinPath."cache/".$mergedCssFileName);		}		else{			foreach(self::$css_files as $cssFile){				if(file_exists(_BASEDIR.self::$skinPath.$cssFile)){					$html .= sprintf('<link rel="stylesheet" type="text/css" href="%s"/>'."\n",_BASEURL.self::$skinPath.$cssFile);				}			}		}		return $html;	}		public static function clearCssJsCache()    {		global $dbObj;		@unlink(_BASEDIR.self::$skinPath."cache/".self::$configData['compressed_js_file']);		@unlink(_BASEDIR.self::$skinPath."cache/".self::$configData['compressed_css_file']);		$dbObj->updateConfigVariable('compressed_js_file','');		$dbObj->updateConfigVariable('compressed_css_file','');		$dbObj->initConfigVariables();		$script = "rm -rf "._BASEDIR.self::$skinPath."cache/*";		$results = system($script,$retval);	}
} 