<?php/** * SLIM_MVC_Framework * * @category  controllers * @package   SLIM_MVC_Framework * @copyright Copyright (c) 2014 (http://www.elamurugan.com/) * @author    Ela <nelamurugan@gmail.com> *//** * Class SLIM_MVC_Framework * * @category    controllers * @package     SLIM_MVC_Framework */class Utils{    /**     * @return array     */    public static function userTypes()    {        $types = array('ADMIN' => 'Admin', 'USER' => 'User');        return $types;    }    public static function yesNoOptions()    {        $types = array('0' => 'No', '1' => 'Yes');        return $types;    }    public static function itemStatusOptions()    {        $types = array('0' => 'Enabled', '1' => 'Disabled');        return $types;    }    /**     * @return bool     */    public static function isInstalled()    {        if (file_exists((_APPDIR . "etc/local.xml"))) {            return true;        }        return false;    }    /**     * @return array     */    public static function getSystemVersion()    {        $xmlObj = new XMLParser();        return explode(".", $xmlObj->getConfig('version'));    }    /**     *     */    public static function checkForSystemUpdate()    {    }}