<?phpclass Utils{    /**     * @return array     */    public static function userTypes()    {        $types = array('ADMIN' => 'Admin', 'USER' => 'User');        return $types;    }    /**     * @return bool     */    public static function isInstalled()    {        if (file_exists((_APPDIR . "local.xml"))) {            return true;        }        return false;    }    /**     * @return array     */    public static function getSystemVersion()    {        $xmlObj = new XMLParser();        return explode(".", $xmlObj->getConfig('version'));    }    /**     *     */    public static function checkForSystemUpdate()    {        $xmlObj = new XMLParser();        $currentVersion = $xmlObj->getConfig('version');    }}